import java.nio.charset.StandardCharsets
import java.text.SimpleDateFormat
import org.apache.nifi.processor.io.StreamCallback
import java.util.concurrent.TimeUnit;

// Create a new flow file since there's no upstream processor providing one
flowFile = session.create()
if (!flowFile) return

def random = new Random()

words = [
    "amazing", "awesome", "beautiful", "brilliant", "clever", "cool",
    "fantastic", "funny", "great", "incredible", "interesting", "kind",
    "lovely", "nice", "perfect", "powerful", "smart", "wonderful"
]

phrases = [
    "can't believe", "so excited about", "just discovered", "finally got", "totally in love with",
    "blown away by", "impressed with", "amazed by", "inspired by", "motivated by",
    "can't wait to see", "looking forward to see", "eager to see", "ready to see", "prepared for"
]

hashtags = [
    "DevOps", "Cloud", "Security", "BigData", "AI", "MachineLearning",
    "DataScience", "Cybersecurity", "IoT", "Blockchain",
    "SoftwareDevelopment", "WebDevelopment", "MobileDevelopment",
    "OpenSource", "Agile", "DevOpsEngineering", "CloudEngineer",
    "SecurityEngineering", "MachineLearningEngineering"
]


// Function to generate a random tweet
def generateTweet(random, words, phrases, hashtags) {
    def tweet = [:]

    // Generate random tweet text using parameters
    def randomWord = words[random.nextInt(words.size())]
    def randomPhrase = phrases[random.nextInt(phrases.size())]
    def randomHashtag = hashtags[random.nextInt(hashtags.size())]

    tweet.text = "${randomPhrase} how ${randomWord} ${randomHashtag} is!"

    // Generate random tweet creation date
    tweet.created_at = new Date().getTime()

    // Generate random tweet id and id_str
    tweet.id = random.nextLong()
    tweet.id_str = tweet.id.toString()

    // Set other random or fixed values for remaining fields
    tweet.truncated = true
    tweet.entities = [:]
    tweet.entities.hashtags = [[text: randomHashtag]]
    tweet.metadata = [iso_language_code: "en", result_type: "recent"]
    tweet.source = "<a href=\"http://www.twitter.com\" rel=\"nofollow\">Twitter Web Client</a>"

    return tweet
}

// Generate tweets by passing random, words, phrases, and hashtags as arguments
def tweets = []
for (i in 0..10) {
    tweets.add(generateTweet(random, words, phrases, hashtags))
}

// Convert to JSON string using JsonBuilder for pretty output
def json = new groovy.json.JsonBuilder(tweets).toPrettyString()

// Write the JSON to the flowfile content using StreamCallback
flowFile = session.write(flowFile, { inputStream, outputStream ->
    outputStream.write(json.getBytes(StandardCharsets.UTF_8))
} as StreamCallback)

// Get sleep time from flow file attribute (assuming attribute name is "sleep.time.ms")
//def sleepTimeString = flowFile.getAttributes().get("sleep.time.ms")
//long sleepTimeInMs = Long.parseLong(sleepTimeString)
Thread.sleep(10)

// Transfer the flow file to the next processor
session.transfer(flowFile, REL_SUCCESS)

import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

// Get the flow file from the session
flowFile = session.get()
if (!flowFile) return

// Read the flow file content and process each line separately
flowFile = session.write(flowFile, { inputStream, outputStream ->
    def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)

    // Split the input by newlines to get each JSON object separately
    def lines = text.split('\n')

    def jsonSlurper = new JsonSlurper()
    def extractedJsonList = []

    // Process each line (assuming each line is a valid JSON object)
    lines.each { line ->
        def jsonObject = jsonSlurper.parseText(line)

        // Extract the first four attributes from the JSON object
        def extractedAttributes = [
            "created_at": jsonObject.created_at,
            "id": jsonObject.id,
            "id_str": jsonObject.id_str,
            "text": jsonObject.text
        ]

        extractedJsonList << extractedAttributes
    }

    // Convert the list of extracted JSON objects to a pretty-printed JSON string
    def newJson = JsonOutput.prettyPrint(JsonOutput.toJson(extractedJsonList))

    // Write the new JSON string to the output stream
    outputStream.write(newJson.getBytes(StandardCharsets.UTF_8))
} as StreamCallback)

// Transfer the flow file to the success relationship
session.transfer(flowFile, REL_SUCCESS)
